mapper.select:
select p.purchaseId, p.purchaseCount, u.userName, d.productName, p.purchaseDate, p.purchaseType, d.productValue, p.purchasePoint, de.deliveryAddress, p.purchaseStatus from purchase p, userinfo u, product d, delivery de
p.userId=u.userId and p.productId=d.productId and p.purchaseId=de.purchaseId
and p.compId=#{compId}
and u.userId=#{userId}
order by p.purchaseDate, p.purchaseId

select p.purchaseId, p.purchaseCount, u.userName, d.productName, p.purchaseDate, p.purchaseType, d.productValue, p.purchasePoint, de.deliveryAddress, p.purchaseStatus
  from purchase p, userinfo u, product d, delivery de

   p.userId=u.userId
   and
   p.productId=d.productId
   and
   p.purchaseId=de.purchaseId

      and p.compId=#{compId}


      and p.purchaseId=#{purchaseId}


      and u.userName=#{userName}


      and p.purchaseStatus=#{purchaseStatus}


  order by p.purchaseDate, p.purchaseId
